 (150, 1, '2020-03-03 13:53:47', '2020-03-03 13:53:47', 'a:10:{s:4:\"type\";s:4:\"text\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:0:\"\";s:11:\"placeholder\";s:0:\"\";s:7:\"prepend\";s:0:\"\";s:6:\"append\";s:0:\"\";s:9:\"maxlength\";s:0:\"\";}', 'Intro Copy', 'intro_copy', 'publish', 'closed', 'closed', '', 'field_5e5e612918dff', '', '', '2020-03-03 13:53:47', '2020-03-03 13:53:47', '', 146, 'https://frontendmike.com/?post_type=acf-field&p=150', 3, 'acf-field', '', 0),
 (151, 1, '2020-03-03 13:53:47', '2020-03-03 13:53:47', 'a:15:{s:4:\"type\";s:5:\"image\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"return_format\";s:3:\"url\";s:12:\"preview_size\";s:9:\"thumbnail\";s:7:\"library\";s:3:\"all\";s:9:\"min_width\";s:0:\"\";s:10:\"min_height\";s:0:\"\";s:8:\"min_size\";s:0:\"\";s:9:\"max_width\";s:0:\"\";s:10:\"max_height\";s:0:\"\";s:8:\"max_size\";s:0:\"\";s:10:\"mime_types\";s:0:\"\";}', 'Intro BG', 'intro_bg', 'publish', 'closed', 'closed', '', 'field_5e5e614618e00', '', '', '2020-03-03 13:53:47', '2020-03-03 13:53:47', '', 146, 'https://frontendmike.com/?post_type=acf-field&p=151', 4, 'acf-field', '', 0),
